import cv2
import glob


def read_video():
    """
    逐帧读取视频文件
    """
    video = './video/input/library.mp4'
    capture = cv2.VideoCapture(video)
    if capture.isOpened():
        while True:
            ret, img = capture.read()  # img 就是一帧图片
            if not ret:
                break  # 当获取完最后一帧就结束
    else:
        print('视频打开失败！')


def write_video():
    """
    将图片拼接成视频输出
    """
    video = './video/output/library.mp4'
    writer = cv2.VideoWriter(video)
    img_paths = glob.glob('./pic/*.jpg')
    for path in img_paths:
        print(path)
        img = cv2.imread(path)
        writer.write(img)  # 读取图片后一帧帧写入到视频中
    writer.release()


def make_video():
    """
    逐帧读取视频并对帧图片进行处理
    将处理后的帧图片重新拼接成视频输出
    """
    video_input = './video/input/library.mp4'
    video_output = './video/output/library.avi'
    capture = cv2.VideoCapture(video_input)
    fourcc = cv2.VideoWriter_fourcc(*'DIVX')
    writer = None
    frame = 0
    if capture.isOpened():
        while True:
            ret, img = capture.read()  # img 就是一帧图片
            if writer is None:
                writer = cv2.VideoWriter(video_output, fourcc, 30.0, (img.shape[1], img.shape[0]), True)
            if not ret:
                break  # 当获取完最后一帧就结束
            img_out = op_img(img, frame)    # 图像处理
            writer.write(img_out)   # 图像输出
            frame += 1
            if frame >= 300:
                break
    else:
        print('视频打开失败！')
    capture.release()
    writer.release()


